func (hds *HTTPDetectorSelect) sqlScan(ctx context.Context, v interface{}) error {
	rows := &sql.Rows{}
	query, args := hds.sqlQuery().Query()
	if err := hds.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (hds *HTTPDetectorSelect) sqlQuery() sql.Querier {
	selector := hds.sql
	selector.Select(selector.Columns(hds.fields...)...)
	return selector
}
